import styled from "styled-components";
import { COLORS, QUERIES } from "../constants";

const CardWrapper = styled.article<CardProps>`
  background-color: ${({ bgc }) => bgc};
`;

const ImageWrapper = styled.div`
`;

const Title = styled.h2`
`;

const Description = styled.p`
`;

const Button = styled.button`
`;

type CardProps = {
  bgc: "string"
}

function Card({ bgc }: CardProps) {
  return (
    <CardWrapper bgc>
      <ImageWrapper>
        <img src="images/icon-sedans.svg" alt="" />
      </ImageWrapper>
      <Title>sedans</Title>
      <Description>
        Choose a Sedan for its affordability and excellence fuel economy. Ideal for cruising in the city or your next trip.
      </Description>
      <Button>Learn Morn</Button>
    </CardWrapper>
  )
};

export default Card;
