import styled from "styled-components";
import { COLORS, QUERIES } from "../../constants";

const Wrapper = styled.article`
  background-color: ${COLORS.primary.cardBgc};
  max-width: 500px;
  display: flex;
  flex-direction: column;
  border-radius: 8px;
  overflow: hidden;

  @media ${QUERIES.laptopAndUp} {
    flex-direction: row-reverse;
    max-width: initial;
  }
`;

const ImageWrapper = styled.div`
  background-color: ${COLORS.primary.softFiolet};

  img {
    opacity: 0.4;
    width: 100%;
    object-fit: contain;
  }

  @media ${QUERIES.laptopAndUp} {
    flex: 1;
  }
`;


const ContentWrapper = styled.main`
  padding: 32px 16px;
  display: flex;
  flex-direction: column;
  align-items: center;

  @media ${QUERIES.laptopAndUp}{
    padding: 32px;
    align-items: flex-start;
    flex: 1;
  }
`;

const MaxWidth = styled.div`

@media ${QUERIES.laptopAndUp}{
    padding: 32px;
    align-items: flex-start;
    flex: 1;
  }
`;

const Title = styled.h2`
  color: white;
  text-align: center;

  span {
    color: ${COLORS.primary.softFiolet}
  }

  @media ${QUERIES.laptopAndUp}{
    font-size: 2.2rem;
    text-align: left;
  }
`;

const Description = styled.p`
  color: ${COLORS.neutral.whiteTranHed};
  margin: 16px 0 32px 0;
  text-align: center;

  @media ${QUERIES.laptopAndUp}{  
    text-align: left;
  }
`;

const Scores = styled.ul`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const Score = styled.li`
  text-align: center;
`;
const ScoreTitle = styled.p`
  font-size: 1.5rem;
  color: ${COLORS.neutral.white};
  font-weight: 700;
`;
const ScoreLabel = styled.span`
  color: ${COLORS.neutral.whiteTranPar};
  text-transform: uppercase;
  font-size: 0.825rem;
`;

function Card() {
  return (
    <Wrapper>
      <ImageWrapper>
        <img src="images/mobile.jpg" alt="backgrund images card" />
      </ImageWrapper>
      <ContentWrapper>
        <MaxWidth>
          <Title>Get <span>insights</span> that help your business grow</Title>
          <Description>Discover the benefits of data analytics and make better decisionregarding revenue, customer expirience, and overall efficiency.</Description>
          <Scores>
            <Score>
              <ScoreTitle>10k+</ScoreTitle>
              <ScoreLabel>companies</ScoreLabel>
            </Score>
            <Score>
              <ScoreTitle>314</ScoreTitle>
              <ScoreLabel>templates</ScoreLabel>
            </Score>
            <Score>
              <ScoreTitle>12M+</ScoreTitle>
              <ScoreLabel>queries</ScoreLabel>
            </Score>
          </Scores>
        </MaxWidth>

      </ContentWrapper>
    </Wrapper>
  )
};

export default Card;
