import styled from "styled-components";
import { COLORS } from "../../constants";

const Wrapper = styled.article`
  background-color: white;
  max-width: 375px;
  border-radius: 16px;
  overflow: hidden;
`;

const Main = styled.main`
  padding-left: 16px;
  padding-right: 16px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled.h2`
  color: ${COLORS.neutral.darkBlue};
  font-size: 1.4rem;
  margin: 24px 0 16px 0;
`;

const Description = styled.p`
  color: ${COLORS.neutral.desaturatedBlue};
  text-align: center;
  font-weight: 500;
  font-size: 0.825rem;
`;

const PlanContainer = styled.div`
  display: grid;
  margin: 16px 0;
  width: 100%;
  grid-template-columns: 50px 1fr max-content;
  padding: 8px;
  gap: 8px;
  align-items: center;
  background-color: ${COLORS.neutral.veryPaleBlue};
  border-radius: 8px;
`;

const PriceWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const PriceTitle = styled.h3`
  font-size: 0.825rem;
`;

const Price = styled.span`
  color: ${COLORS.neutral.desaturatedBlue};
  font-size: 0.725rem;
`;

const PriceButton = styled.button`
  background-color: transparent;
  border: none;
  outline: none;
  color: ${COLORS.primary.brightBlue};
  font-weight: 700;
  font-size: 0.825rem;
  border-bottom: 1px solid currentColor;
`;

const ProceedButton = styled.button`
  background-color: ${COLORS.primary.brightBlue};
  border: none;
  color: white;
  font-size: 0.825rem;
  font-weight: 700;
`;

function Card() {
  return (
    <Wrapper>
      <img src="images/illustration-hero.svg" alt="background" />
      <Main>
        <Title>Order Summary</Title>
        <Description>You can now listento milonos of songs, audiobooks and podcasts on any devices awrewhere you like!</Description>
        <PlanContainer>
          <img src="images/music.svg" alt="music icon" />
          <PriceWrapper>
            <PriceTitle>Annual Plan</PriceTitle>
            <Price>$59.99/year</Price>
          </PriceWrapper>
          <PriceButton>Change</PriceButton>
        </PlanContainer>
        <ProceedButton>Proceed to Payment</ProceedButton>
      </Main>
    </Wrapper>
  )
};

export default Card;
