import styled from "styled-components";
import { COLORS } from "../../constants";

const Wrapper = styled.article`
  background-color: ${COLORS.neutral.blueCard};
  padding: 32px;
  border-radius: 16px;
`;

const ImageWrapper = styled.div`
  border-radius: 8px;
  overflow: hidden;
`;

const Title = styled.h2`
  color: ${COLORS.neutral.white};
  font-size: 1rem;
  margin: 16px 0;
`;

const Description = styled.p`
  font-size: 0.8250rem;
  color: ${COLORS.primary.softBlue};
  font-weight: 300;
`;

const List = styled.ul`
  display: flex;
  position: relative;
  justify-content: space-between;
`;

const ListItem = styled.li`
  display: inline-flex;
  margin-top: 8px;
  align-items: center;
  gap: 8px;
`;

const ItemTitle = styled.p`
  font-size: 0.875rem;
  color: ${COLORS.primary.cyan};
`;

const StyledItemTitle = styled(ItemTitle)`
  color: ${COLORS.primary.softBlue};

  &::after {
    display: block;
    position: absolute;
    content: '';
    height: 1px;
    width: 100%;
    background-color: ${COLORS.primary.softBlue};
  }
`;


function Card() {
  return (
    <Wrapper>
      <ImageWrapper>
        <img src="images/equilibrium.jpg" alt="equilibrium" />
      </ImageWrapper>
      <Title>Equilibrium #3429</Title>
      <Description>Our Equilibrium collection promotes balance and calm.</Description>
      <List>
        <ListItem>
          <img src="images/ethereum.svg" alt="ethereum" />
          <ItemTitle>0.041ETH</ItemTitle>
        </ListItem>
        <ListItem>
          <img src="images/clock.svg" alt="clock" />
          <StyledItemTitle>3 deys left</StyledItemTitle>
        </ListItem>
      </List>
    </Wrapper>
  )
};

export default Card;
