import styled from "styled-components";
import { COLORS } from "../../constants";

const Wrapper = styled.article`
  background-color: ${COLORS.neutral.blueCard};
  padding: 32px;
  border-radius: 16px;
`;

const ViewImage = styled.div`
  position: absolute;
  top: 0;
  bottom: 0;
  right: 0;
  left: 0;
  margin: auto;
  width: 30px;
  height: 30px;
  z-index: 9999;
  display: none;
`;

const ImageWrapper = styled.div`
  position: relative;
  background-color: ${COLORS.primary.cyan};
  border-radius: 8px;
  overflow: hidden;
  cursor: pointer;

  &:hover img{
    opacity: 0.3;
  }

  &:hover ${ViewImage} {
    display: block;
    z-index: 9999;
  }
`;

const Title = styled.h2`
  color: ${COLORS.neutral.white};
  width: max-content;
  cursor: pointer;
  font-size: 1rem;
  margin: 16px 0;
  transition: color .3s ease;

  &:hover {
    color: ${COLORS.primary.cyan};
  }
`;

const Description = styled.p`
  font-size: 0.8250rem;
  color: ${COLORS.primary.softBlue};
  font-weight: 300;
`;

const List = styled.ul`
  display: flex;
  position: relative;
  justify-content: space-between;
`;

const ListItem = styled.li`
  display: inline-flex;
  margin-top: 8px;
  align-items: center;
  gap: 8px;
`;

const ItemTitle = styled.p`
  font-size: 0.875rem;
  color: ${COLORS.primary.cyan};
`;

const StyledItemTitle = styled(ItemTitle)`
  color: ${COLORS.primary.softBlue};

  &::after {
    display: block;
    position: absolute;
    margin-top: 8px;
    left: 0;
    content: '';
    height: 1px;
    width: 100%;
    background-color: ${COLORS.neutral.blueLine};
  }
`;

const Footer = styled.footer`
  display: flex;
  align-items: center;
  margin-top: 24px;
  gap: 8px;

  p {
    font-size: 0.825rem;
    color: ${COLORS.primary.softBlue};
  }

  p strong {
    color: ${COLORS.neutral.white};
    cursor: pointer;
    transition: color .3s ease;

    &:hover {
      color: ${COLORS.primary.cyan};
    }
  }
`;

const Avatar = styled.div`
  width: 50px;
  height: 50px;
  border: 1px solid ${COLORS.neutral.white};
  border-radius: 50%;
`;

const InnerWrapper = styled.main`
  max-width: 300px;
`;


function Card() {
  return (
    <Wrapper>
      <InnerWrapper>
        <ImageWrapper>
          <img src="images/equilibrium.jpg" alt="equilibrium" />
          <ViewImage>
            <img src="images/view.svg" alt="view" />
          </ViewImage>
        </ImageWrapper>
        <Title>Equilibrium #3429</Title>
        <Description>Our Equilibrium collection promotes balance and calm.</Description>
        <List>
          <ListItem>
            <img src="images/ethereum.svg" alt="ethereum" />
            <ItemTitle>0.041ETH</ItemTitle>
          </ListItem>
          <ListItem>
            <img src="images/clock.svg" alt="clock" />
            <StyledItemTitle>3 deys left</StyledItemTitle>
          </ListItem>
        </List>
        <Footer>
          <Avatar>
            <img src="images/avatar.png" alt="avatar" />
          </Avatar>
          <p>Creation of <strong>Jules Wyvern</strong></p>
        </Footer>
      </InnerWrapper>
    </Wrapper>
  )
};

export default Card;
