import styled from "styled-components";
import { COLORS } from "../../constants";

const Wrapper = styled.article`
  background-color: white;
  max-width: 375px;
`;

const Main = styled.main`
  padding-left: 16px;
  padding-right: 16px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled.h2`
  color: ${COLORS.neutral.darkBlue};
  font-size: 1.4rem;
  margin: 24px 0 16px 0;
`;

const Description = styled.p`
  color: ${COLORS.neutral.desaturatedBlue};
  text-align: center;
  font-weight: 500;
  font-size: 0.825rem;
`;

const PlanContainer = styled.div`
  display: grid;
  background-color: ${COLORS.neutral.veryPaleBlue};
`;

function Card() {
  return (
    <Wrapper>
      <img src="images/illustration-hero.svg" alt="background" />
      <Main>
        <Title>Order Summary</Title>
        <Description>You can now listento milonos of songs, audiobooks and podcasts on any devices awrewhere you like!</Description>
        <PlanContainer>
          <img src="images/music.svg" alt="music icon" />
          <h3>Annual Plan</h3>
          <span>$59.99/year</span>
          <button>Change</button>
        </PlanContainer>
      </Main>
    </Wrapper>
  )
};

export default Card;
